pan class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;assert&lt;/span&gt; P*Q == N&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;D = C**&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt; - &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;LS_P = gmpy2.legendre(D,P)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;LS_Q = gmpy2.legendre(D,Q)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;d = gmpy2.invert(E, gmpy2.lcm(P-LS_P, Q-LS_Q))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;inv_q, inv_p = inverse_mod(P, Q), inverse_mod(Q, P)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;rp, rq = lucas(k=inverse_mod(E, P-LS_P), P=C, Q=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, n=P)[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], lucas(k=inverse_mod(E, Q-LS_Q), P=C, Q=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, n=Q)[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;r = crt(&lt;span class=&#34;built_in&#34;&gt;int&lt;/span&gt;(rp),&lt;span class=&#34;built_in&#34;&gt;int&lt;/span&gt;(rq),P,Q)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;vp=lucas(k=E, P=r, Q=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, n=P*P)[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;tmp_p = C * inverse_mod(&lt;span class=&#34;built_in&#34;&gt;int&lt;/span&gt;(vp),P*P) %(P*P)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;tmp_p = &lt;span class=&#34;built_in&#34;&gt;int&lt;/span&gt;(tmp_p - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) // P&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp = &lt;span class=&#34;built_in&#