).hexdigest().encode()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;num1 = 5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;p = int(gmpy2.next_prime(bytes_to_long(key1 + os.urandom(64))))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ms = [random.getrandbits(256) for _ in range(num1)]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;qs = [getPrime(1024) for _ in range(num1)]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ns = [p * qs[_] + ms[_] for _ in range(num1)]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;num2 = 37&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x = bytes_to_long(key2 + os.urandom(32))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;A = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;B = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;for i in range(num2):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    a = random.getrandbits(512)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    b = a * x % p&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    gift = (2 ** 128 - 1) * 2 ** 400&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    A.append(a)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    B.append((b &amp;amp; gift) &amp;gt;&amp;gt; 400)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;bh + bm + bl = a * x + kp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;bh * 2^528 + bl = a * x - bm * 2^400 + kp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;238          400&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;iv = long_to_bytes(random.getrandbits(128))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;key = xor(key1,key2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;lin