s(1024))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;X = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;for i in range(n):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    X.append(random.getrandbits(1024))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;p = getPrime(1026)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def f(x):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    res = 0&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    tmp = 1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for i in range(n):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        res = (res + tmp * A[i]) % p &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        tmp = tmp *x  % p&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return res % p&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# res = a0+a1*x+a2*x^2+a3*x3+...+a20*x20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;R = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;for i in range(n):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    R.append(f(X[i]))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;P = secret&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;Q = getPrime(1024)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;N = P * Q&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;m = bytes_to_long(flag)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;e = 65537&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;c = pow(m, e, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;phi=(P - 1) * (Q - 1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;d = pow(e,-1,phi)&lt